		var d = b.split(":")[1];
		var c = b.split(":")[0];
		switch (d) {
		  case "append":
			$(c).append(a);
			break;
		  case "prepend":
			$(c).prepend(a);
			break;
		  case "before":
			$(a).insertBefore(c);
			break;
		  case "after":
			$(a).insertAfter(c);
			break;
		  default:
			$(c).html(a)
		}
	  } else $(b).html(a)
		};
