	WSAIO.DiscountCode.toggle = function (state) {
	  if ('undefined' === typeof state) var state = 'show';
	  if ($ws.discount_code_field_selector.indexOf(':') > -1) {
		(state === 'show' ? ($($ws.discount_code_field_selector.split(":")[0]).show()) : ($($ws.discount_code_field_selector.split(":")[0]).hide()));
	  } else {
		(state === 'show' ? ($($ws.discount_code_field_selector).show()) : ($($ws.discount_code_field_selector).hide()));
	  }
	}

	WSAIO.checkCustomer = function (type, tags, excluded) {
	  if (WSAIO.debugger) debugger;
	  switch (type) {
		case 'has_tag':
		  if ($ws.customer.id) {
			var isTag_all = tags.indexOf("all");
			var isTag_has_account = tags.indexOf("has_account");
			var isTag = [];
			try {
			  isTag = tags.map(function (tag) {
				return $ws.customer.tags.findIndex(function (tag2) {
				  return tag.toLowerCase() === tag2
				}) > -1
			  });
			} catch (error) {}
			if (isTag.indexOf(true) > -1) {
			  $ws.applied_tag = tags[isTag.indexOf(true)];
			  $ws.applied_tag_array.push(tags[isTag.indexOf(true)]);
			  return isTag.indexOf(true) > -1
			} else if (isTag_all > -1) {
				$ws.applied_tag_array.push('all');
			  return true;
			} else if (isTag_has_account > -1) {
				$ws.applied_tag_array.push('has_account');
			  $ws.applied_tag = 'has_account';
			  return true;
			} else {
			  return isTag.indexOf(true) > -1
			}
		  } else {
			var isTag_all = tags.indexOf("all");
			var isTag_no_account = tags.indexOf("no_account");
			if (isTag_no_account > -1) {
			  $ws.applied_tag = 'no_account';
			  $ws.applied_tag_array.push('no_account');
			  return true;
			} else if (isTag_all > -1) {
				$ws.applied_tag_array.push('all');
			  return true;
			} else {
			  return false;
			}
		  }

		  break;
		case 'has_account':
		  $ws.applied_tag = "has_account";
		  $ws.applied_tag_array.push("has_account");
		  if ($ws.customer.id) {
			var isTag = [];
			try {
			  isTag = excluded.map(function (tag) {
				return $ws.customer.tags.findIndex(function (tag2) {
				  return tag.toLowerCase() === tag2
				}) > -1
			  });
			} catch (error) {}
			if (isTag.indexOf(true) > -1) {
			  $ws.excluded_tag = excluded[isTag.indexOf(true)];
			}
			return isTag.indexOf(true) === -1
		  } else return false;
		  break;
		case 'no_account':
		  $ws.applied_tag = "no_account";
		  $ws.applied_tag_array.push("no_account");
		  return $ws.customer.id ? false : true;
		  break;
		case 'all':
		  if ($ws.customer.id) {
			var isTag = [];
			try {
			  isTag = excluded.map(function (tag) {
				return $ws.customer.tags.findIndex(function (tag2) {
				  return tag.toLowerCase() === tag2
				}) > -1
			  });
			} catch (error) {}
			if (isTag.indexOf(true) > -1) {
			  $ws.excluded_tag = excluded[isTag.indexOf(true)];
			}
			return isTag.indexOf(true) === -1
		  } else return true;
		  break;
		default:
		  if ($ws.customer.id) {
			var isTag = [];
			try {
			  isTag = excluded.map(function (tag) {
				return $ws.customer.tags.findIndex(function (tag2) {
				  return tag.toLowerCase() === tag2
				}) > -1
			  });
			} catch (error) {}
			if (isTag.indexOf(true) > -1) {
			  $ws.excluded_tag = excluded[isTag.indexOf(true)];
			}
			return isTag.indexOf(true) === -1
		  } else return true;
		  break;
	  }
	};

    WSAIO.remove_comparePrice = function(html){
        try{
            var jqWrapped = $('<div>'+html+'</div>');
            jqWrapped.find('.money').filter((i, x)=>/%compare_at_price%/gi.test(x.innerText) ).removeClass('money');
            return jqWrapped.html();
        }catch(e){
            console.log(e);
            return html;
        }
    }

	WSAIO.checkAppliesTo = function (params) {
	  if (WSAIO.debugger) debugger;
	  switch (params.type) {
		case "products":
		  params.rule["priority"] = 1;
		  applies_to_products();
		  break;
		case "collections":
		  params.rule["priority"] = 2;
		  applies_to_collections();
		  break;
		case "entire_store":
		  var excluded = false;
		  if (params.rule.exclude_products && params.rule.exclude_products.findIndex(function (x) {
			return x.handle == $ws.selected_product.handle
		  }) > -1) {
			excluded = true;
		  }
		  if (params.rule.exclude_collections && params.rule.exclude_collections.length > 0) {
			for (var xzy = 0; xzy < $ws.product_in_collects.length; xzy++) {
			  var item = $ws.product_in_collects[xzy];
			  if (-1 < params.rule.exclude_collections.findIndex(function (x) {
				return x.id == item.collection_id
			  })) {
				if ($ws.selected_product.id == item.product_id) {
				  excluded = true;
				}
			  }
			}
		  }
		  if (!excluded) {
			params.rule["priority"] = 3;
			if (params.module === "regular_discount") {
			  $ws.productRd.push(params.rule);
			} else {
			  $ws.productVd.push(params.rule);
			}
		  }
		  break;
		default:
		  params.rule["priority"] = 3;
		  if (params.module === "regular_discount") {
			$ws.productRd.push(params.rule);
		  } else {
			$ws.productVd.push(params.rule);
		  }
		  break;
	  }

	  function applies_to_products() {
		if (WSAIO.debugger) debugger;
		// Apply only if selected_product_id was defined.
		if ('undefined' !== typeof $ws.selected_product.handle || 'undefined' !== typeof $ws.selected_product.id) {
		  var isFound = params.rule.products.findIndex(function (x) {
			return (x.handle === $ws.selected_product.handle || x.id == $ws.selected_product.id)
		  })
		  if (isFound > -1) {
			if (params.module === "regular_discount") {
			  if (params.rule.products[isFound].variant_level === true) {
				$ws.productRd = [];
			  }
			  $ws.productRd.push(params.rule);
			} else {
			  if (params.rule.products[isFound].variant_level === true) {
				$ws.productVd = [];
			  }
			  $ws.productVd.push(params.rule);
			}
		  }
		} else {
		  isError("Selected product handle is not defined.");
		}
	  }

	  function applies_to_collections() {
		if (WSAIO.debugger) debugger;
		var isFound = [false];
		// if selected_product_id is defined
		if ($ws.selected_product.handle) {
		  var excluded = false;
		  if (params.rule.exclude_products && params.rule.exclude_products.findIndex(function (x) {
			return x.handle == $ws.selected_product.handle
		  }) > -1) {
			excluded = true;
		  }
		  if (!excluded) {
			isFound = params.rule.collections.map(function (collection, i) {
			  return -1 < ($ws.product_in_collects.findIndex(function (x) {
				return (x.product_handle == $ws.selected_product.handle || x.product_id == $ws.selected_product.id) && x.collection_id == collection.id
			  }))
			});
			if (isFound.indexOf(true) > -1) {
			  if (params.module === "regular_discount") {
				$ws.productRd.push(params.rule);
			  } else {
				$ws.productVd.push(params.rule);
			  }
			}
		  }
		}
		// if only selected_collection.id is defined.
		else if ($ws.template === "collection" && $ws.selected_collection.id) {
		  // $ws.selected_collection.id (if template is collection then this id contains current collection id, otherwise null). for other case
		  isFound = params.rule.collections.map(function (collection, i) {
			return collection.id == $ws.selected_collection.id;
		  });
		  if (isFound.indexOf(true) > -1) {
			if (params.module === "regular_discount") {
			  $ws.productRd.push(params.rule);
			} else {
			  $ws.productVd.push(params.rule);
			}
		  }
		} else {
		  isError("Missing either product's id or collection's id or template is not set for collections");
		}
	  }
	  if (params.module === "regular_discount") {
		if ($ws.productRd && $ws.productRd.length > 1) {
		  if (-1 < $ws.productRd.findIndex(function (z) {
			return z.individual_product === true
		  })) {
			$ws.productRd = $ws.productRd.filter(function (x) {
			  return x.individual_product === true
			});
		  } else if (-1 < $ws.productRd.findIndex(function (z) {
			return z.customer_group === 'has_tag'
		  })) {
			$ws.productRd = $ws.productRd.filter(function (x) {
			  return x.customer_group === 'has_tag'
			});
		  } else if (-1 < $ws.productRd.findIndex(function (z) {
			return z.customer_group === 'has_tag'
		  })) {
			$ws.productRd = $ws.productRd.filter(function (x) {
			  return x.customer_group === 'has_tag'
			});
		  } else if (-1 < $ws.productRd.findIndex(function (z) {
			return z.customer_group === 'has_account'
		  })) {
			$ws.productRd = $ws.productRd.filter(function (x) {
			  return x.customer_group === 'has_account'
			});
		  } else if (-1 < $ws.productRd.findIndex(function (z) {
			return z.customer_group === 'no_account'
		  })) {
			$ws.productRd = $ws.productRd.filter(function (x) {
			  return x.customer_group === 'no_account'
			});
		  }
		}
	  } else {
		if ($ws.productVd && $ws.productVd.length > 1) {
		  if (-1 < $ws.productVd.findIndex(function (z) {
			return z.individual_product === true
		  })) {
			$ws.productVd = $ws.productVd.filter(function (x) {
			  return x.individual_product === true
			});
		  } else if (-1 < $ws.productVd.findIndex(function (z) {
			return z.customer_group === 'has_tag'
		  })) {
			$ws.productVd = $ws.productVd.filter(function (x) {
			  return x.customer_group === 'has_tag'
			});
		  } else if (-1 < $ws.productVd.findIndex(function (z) {
			return z.customer_group === 'has_account'
		  })) {
			$ws.productVd = $ws.productVd.filter(function (x) {
			  return x.customer_group === 'has_account'
			});
		  } else if (-1 < $ws.productVd.findIndex(function (z) {
			return z.customer_group === 'no_account'
		  })) {
			$ws.productVd = $ws.productVd.filter(function (x) {
			  return x.customer_group === 'no_account'
			});
		  }
		}
	  }
	};

	  WSAIO.price_with_unit = function(price){
		  if(typeof WSAIO.country_currency_code != 'undefined' && typeof WSAIO.customer.default_address.country_code != 'undefined'){
				price = price+' '+WSAIO.country_currency_code[WSAIO.customer.default_address.country_code].split('_')[0];
		  }else{
			price = price+' '+Shopify.currency.active;
		  }
		  return price;
	   };
	  WSAIO.change_currency_sign = function(price){
			  if(typeof WSAIO.country_currency_code != 'undefined' && typeof WSAIO.customer.default_address.country_code != 'undefined' && typeof WSAIO.show_currenyLabel_withPrice != 'undefined' && WSAIO.show_currenyLabel_withPrice && typeof Shopify.currency != 'undefined' && Shopify.currency.active != 'undefined' && WSAIO.formatMoney('0').indexOf(Shopify.currency.active) == -1 && typeof WSAIO.market_selected != 'undefined' && typeof WSAIO.customer.default_address.country_code != 'undefined' && (WSAIO.market_selected != WSAIO.customer.default_address.country_code)){
				   var character = null;
			  price = price.toString();
				   character= price.replace( /(<([^>]+)>)/ig, '');
					character= character.replace(/\d.+/g, '');
					character= character.replace(/\d,+/g, '')
				  price = price.replace(character,WSAIO.country_currency_code[WSAIO.customer.default_address.country_code].split('_')[1])
		  }
		  return price;
	   }

	WSAIO.volumeDiscount = function (params, callback) {
	  if (WSAIO.debugger) debugger;
	  if (typeof params === 'function') callback = params;
	  if(!params){
		isError("No params provided for volume discount");
		return false;
	  }
	  if(!params.item_price_selector){
		isError("Item price selector is undefined");
		return false;
	  }
	  if (WSAIO.isWholesaleCustomer() === false) {
		if (typeof callback === "function") {
		  return callback({
			error: "Invalid customer"
		  });
		} else {
		  return "Invalid customer";
		}
	  }
      var WSAIO_variant_sku_are_same = true;

	  if($(params.item_price_selector).attr('class') != undefined){
		if($(params.item_price_selector).attr('class').indexOf('wsaio_') == -1){
		  $(params.item_price_selector).addClass('wsaio_'+params.product_id)
		}
	  }

	  if (typeof params.template !== 'undefined' && typeof WSAIO.auto_vat_enable != 'undefined' &&  WSAIO.auto_vat_enable){
		var _id = params.product_id;
		if(WSAIO.template != 'cart' && typeof WSAIO.product_in_collects != 'undefined' && WSAIO.product_in_collects.length != 0){
			 var _indx = WSAIO.product_in_collects.findIndex(function(e,r){
				 return e.product_id == _id;
			 });
			 if(typeof WSAIO.product_in_collects[_indx] != 'undefined' && typeof WSAIO.product_in_collects[_indx].product_variants != 'undefined'){
				 var  current_product_variant = WSAIO.product_in_collects[_indx].product_variants;
				 var _indx_of_variant = current_product_variant.findIndex(function(d,t){
					 return d.id == params.variant_id;
				 })
				 WSAIO.selected_variant_taxable = current_product_variant[_indx_of_variant].taxable;
				 // if Price set in price adjustment, then no tax include function run
				 if(typeof WSAIO.tax_settings != 'undefined' && WSAIO.tax_settings.presentment != null && WSAIO.tax_settings.presentment.price_adjustments != null && WSAIO.tax_settings.presentment.price_adjustments.fixed_variant_ids.length && WSAIO.tax_settings.presentment.price_adjustments.fixed_variant_ids.indexOf(current_product_variant[_indx_of_variant].id) > -1){
					WSAIO.selected_variant_taxable = false;
				}
			 }
		 }else if(typeof WSAIO.products_with_collections != 'undefined' && WSAIO.products_with_collections.length != 0){
			 var _indx = WSAIO.products_with_collections.findIndex(function(e,r){
				 return e.product_id == _id;
			 });
			 if(typeof WSAIO.products_with_collections[_indx] != 'undefined' && typeof WSAIO.products_with_collections[_indx].product_variants != 'undefined'){
				 var  current_product_variant = WSAIO.products_with_collections[_indx].product_variants;
				 var _indx_of_variant = current_product_variant.findIndex(function(d,t){
					 return d.id == params.variant_id;
				 })
				 WSAIO.selected_variant_taxable = current_product_variant[_indx_of_variant].taxable;
				 // if Fixed Price set in price adjustment, then no tax include function run
				 if(typeof WSAIO.tax_settings != 'undefined' && WSAIO.tax_settings.presentment != null && WSAIO.tax_settings.presentment.price_adjustments != null && WSAIO.tax_settings.presentment.price_adjustments.fixed_variant_ids.length && WSAIO.tax_settings.presentment.price_adjustments.fixed_variant_ids.indexOf(current_product_variant[_indx_of_variant].id) > -1){
					WSAIO.selected_variant_taxable = false;
				}
			 }
		 }
   }
	  $ws.output = true;
	  $ws.remove_duplicate_table = false;
	  $ws.selected_variant = JSON.parse(JSON.stringify($ws.current_variant)); // current variant obj should not change
	  $ws.selected_product = JSON.parse(JSON.stringify($ws.current_product)); // current product obj should not change
	  $ws.selected_collection = JSON.parse(JSON.stringify($ws.current_collection)); // current collection obj should not change
	  if (typeof params === 'object') {
		if (typeof params.product_id !== 'undefined' && params.product_id !== "default") $ws.selected_product.id = params.product_id;
		if (typeof params.product_handle !== 'undefined' && params.product_handle !== "default") $ws.selected_product.handle = params.product_handle;
		if (typeof params.variant_id !== 'undefined' && params.variant_id !== "default") $ws.selected_variant.id = params.variant_id;
		if (typeof params.variant_sku !== 'undefined' && params.variant_sku !== "default") $ws.selected_variant.sku = params.variant_sku;
		if (typeof params.variant_price !== 'undefined' && params.variant_price !== "default") $ws.selected_variant.price = params.variant_price;
		if (typeof params.variant_compare_at_price !== 'undefined' && params.variant_compare_at_price !== "default") $ws.selected_variant.compare_at_price = params.variant_compare_at_price;
		if (typeof params.collection_id !== 'undefined' && params.collection_id !== "default") $ws.selected_collection.id = params.collection_id;
		if (typeof params.output !== 'undefined' && params.output !== "default") $ws.output = params.output;
		if (typeof params.product_parent_grid_selector !== 'undefined' && params.product_parent_grid_selector !== "default") $ws.product_qb_table_selector = params.qb_table_selector;
		if (typeof params.remove_duplicate_table !== 'undefined' && params.remove_duplicate_table !== "default") $ws.remove_duplicate_table = params.remove_duplicate_table;
		if (typeof params.template !== 'undefined' && params.template !== "default") $ws.landingTemplate = params.template;
	  }
	  if ($ws.selected_variant && typeof $ws.selected_variant.price === "string" && $ws.selected_variant.price.indexOf(".") > -1) {
		$ws.selected_variant.price = $ws.selected_variant.price.replace('.', '');
	  }
	  if ($ws.selected_variant && typeof $ws.selected_variant.compare_at_price === "string" && $ws.selected_variant.compare_at_price.indexOf(".") > -1) {
		$ws.selected_variant.compare_at_price = $ws.selected_variant.compare_at_price.replace('.', '');
	  }
	  $ws.productVd = [];

      var hasImportRule = $ws.volume_discounts.some(function(item){
		return item.applies_to == "products" && item.products.length && item.products[0].id == item.products[0].handle;
      });

      if(hasImportRule){
        $ws.volume_discounts.sort(function (a, b) {
            // Shifting import rule to end
            if(b.applies_to == "products" || a.applies_to == "products"){
                if(a.products.length && (a.products[0].id == a.products[0].handle)){
                    return 1;
                }
                if(b.products.length && (b.products[0].id == b.products[0].handle)){
                    return -1;
                }
            }
            return 0;
        });
      }
	  $ws.applied_tag_array = [];
	  // for loop
	  for (var i = 0, len_i = $ws.volume_discounts.length; i < len_i; i++) {
		var volume_discount = $ws.volume_discounts[i];
		if (volume_discount.state !== 'published') continue;
		if ($ws.checkCustomer(volume_discount.customer_group, volume_discount.tags, volume_discount.tags_excluded) === false) continue;
		if (volume_discount.schedule_active) {
		  var _schedule = $ws.getSchedule(volume_discount.schedule);
		  log("_schedule", _schedule);
		  if (_schedule.ends_in) {
			if (
			  Number(_schedule.ends_in.years) <= 0 &&
			  Number(_schedule.ends_in.months) <= 0 &&
			  Number(_schedule.ends_in.days) <= 0 &&
			  Number(_schedule.ends_in.hours) <= 0 &&
			  Number(_schedule.ends_in.minutes) <= 0 &&
			  Number(_schedule.ends_in.seconds) <= 0
			) {
			  log("This rule was expired", _schedule);
			  continue;
			}
			if (
			  Number(_schedule.started.years) >= 0 &&
			  Number(_schedule.started.months) >= 0 &&
			  Number(_schedule.started.days) >= 0 &&
			  Number(_schedule.started.hours) >= 0 &&
			  Number(_schedule.started.minutes) >= 0 &&
			  Number(_schedule.started.seconds) >= 0
			) {
			  log("This rule was schedules and will be started in", _schedule);
			  continue;
			}
		  }
		}
		$ws.checkAppliesTo({
		  "type": volume_discount.applies_to,
		  "rule": volume_discount,
		  "module": "volume_discount"
		});
	  } // end for loop
	  if ($ws.productVd.length > 0) {
		// sort array by priority
		$ws.productVd.sort(function (a, b) {
		  return Number(a.priority) - Number(b.priority)
		});
		// if this array contains multiple applies to, then filter by products or by collection, to exclude extra rules.
		if (-1 < $ws.productVd.findIndex(function (x) {
		  return x.applies_to == "products"
		})) {
		  $ws.productVd = $ws.productVd.filter(function (x) {
			return x.applies_to === "products"
		  });
		} else if (-1 < $ws.productVd.findIndex(function (x) {
		  return x.applies_to == "collections"
		})) {
		  $ws.productVd = $ws.productVd.filter(function (y) {
			return y.applies_to === "collections"
		  });
		}
	  }
	  $ws.product_qb = [];
	  $ws.product_volume_discount = {};
	  $ws.vd_has_discount = false; // assume this has
	  if ($ws.productVd && $ws.productVd.length > 1) {
         var rule_date_found = $ws.productVd.some(function(e,r){ return typeof e.created_at !== "undefined"});
        if(rule_date_found){
          var update_sorted_rule = JSON.parse(JSON.stringify($ws.productVd));
          try{
            update_sorted_rule.sort(function(a,b){
                if(typeof a.created_at === 'undefined' || typeof b.created_at === 'undefined'){
                  return 0;
                }else{
                    if(new Date(a.created_at) > new Date(b.created_at)){
                       return -1;
                    }else{
                        return 1;
                    }
                }
            });
          } catch(e){ update_sorted_rule = []; }

          if(update_sorted_rule.length > 0){
            $ws.productVd = JSON.parse(JSON.stringify(update_sorted_rule));
          }

        }
		$ws.productVd = [$ws.productVd[0]];
	  }

      var hasImported_Rule = $ws.productVd.some(function(item){
        return item.applies_to == "products" && item.products.length && item.products[0].id == item.products[0].handle;
      });
	  if(hasImported_Rule || WSAIO.variant_sku_are_same == false){
		WSAIO_variant_sku_are_same = false;
	  }

	  // Checking priority of volume discount. 
	  $ws.volumeDiscountPriority();
	  if ($ws.productVd && $ws.productVd.length > 0) {
		$ws.vd_has_discount = true;
		// for loop
		for (var j = 0, len_j = $ws.productVd.length; j < len_j; j++) {
		  var vd_rule = $ws.productVd[j];
		  if ("products" === vd_rule.applies_to && vd_rule.individual_product === true) {
			for (var kk = 0; kk < vd_rule.products.length; kk++) {
			  var product = vd_rule.products[kk],
				  pIndex = kk;
			  if (product.handle === $ws.selected_product.handle || product.id == $ws.selected_product.id) {
				if ($ws.product_volume_discount.handle !== product.handle) {
				  $ws.product_volume_discount = JSON.parse(JSON.stringify(vd_rule));
				  $ws.product_volume_discount["product"] = product;
				}
				if (product.variant_level) {
				  for (var ll = 0; ll < product.variants.length; ll++) {
					var variant = product.variants[ll],
						vIndex = ll;
					var check_tag = false;
					var vIn = variant.tag_options.findIndex(function (apt) {
						$ws.applied_tag_array.findIndex(function (single_tag){
							if(apt.name.toString().toLowerCase() == single_tag.toString().toLowerCase())
							{
								check_tag = true;
							}
						})
						if(check_tag){
							return true;
						}
					});
					if (vIn > -1 && (variant.id == $ws.selected_variant.id) || (variant.sku == $ws.selected_variant.sku && !WSAIO_variant_sku_are_same)) {
					  var v_tag_option = variant.tag_options[vIn];
					  $ws.product_qb.push({
						name: v_tag_option.name,
						value: v_tag_option.value,
						type: v_tag_option.type,
						qty: variant.quantity
					  });
					}
				  }
				} else {
					var check_tag = false;
					var pIn = product.tag_options.findIndex(function (apt) {
					  $ws.applied_tag_array.findIndex(function (single_tag){
						  if(apt.name.toString().toLowerCase() == single_tag.toString().toLowerCase())
						  {
							  check_tag = true;
						  }
					  })
					  if(check_tag){
						  return true;
					  }
					});
				  if (pIn > -1) {
					var tag_option = product.tag_options[pIn];
					$ws.product_qb.push({
					  name: tag_option.name,
					  value: tag_option.value,
					  type: tag_option.type,
					  qty: product.quantity
					});
				  }
				}
			  }
			}
		  } else {
			for (var pp = 0; pp < vd_rule.volume_discount.length; pp++) {
			  var vd = vd_rule.volume_discount[pp],
				  vdIndex = pp;
			  $ws.product_qb.push({
				name: vd_rule.applies_to,
				value: vd.value,
				type: vd_rule.discount_type,
				qty: vd.qty
			  });

			}
			$ws.product_volume_discount = JSON.parse(JSON.stringify(vd_rule));
			if (vd_rule.applies_to === "collections") {
			  $ws.product_volume_discount["collections"] = $ws.product_volume_discount.collections.filter(function (c) {
				return c.id == $ws.selected_collection.id
			  });
			}
		  }
		}
	  }
	  $ws.product_volume_discount["volume_discount"] = JSON.parse(JSON.stringify($ws.product_qb));
	  // if TRUE, app will alter prices. our app will output price and compare at price
	  if ($ws.output && $ws.vd_has_discount && (typeof $ws.product_volume_discount.display_option != 'undefined' && $ws.product_volume_discount.display_option != 'no_volume_table')) {
		if ($ws.landingTemplate === "product" || (window.location.href.indexOf('/pages/') > -1 && $ws.landingTemplate === "collection")) {
		  // Remove the table, also check for the quick view
		  if ($ws.remove_duplicate_table) {
			$(".wholesale-table").remove();
		  }
		  if (typeof $ws.product_volume_discount === 'undefined') isError("Volume discount table could not created. Because the rule is empty or not defined. Reference variable is WSAIO.product_volume_discount.");
		  switch ($ws.product_volume_discount.display_option) {
			case "detailed_grid":
			  $ws.detailed_grid_table($ws.product_volume_discount, params);
			  break;
			case "basic_grid":
			  $ws.basic_grid_table($ws.product_volume_discount, params);
			  break;
			case "grid_range":
			  $ws.grid_range_table($ws.product_volume_discount, params);
			  break;
			case "detailed_grid_percent":
			  $ws.detailed_grid_percent_table($ws.product_volume_discount, params);
			  break;
			case "percent_grid":
			  $ws.percent_grid_table($ws.product_volume_discount, params);
			  break;
			case "grid_range_alternate":
			  $ws.grid_range_alternate_table($ws.product_volume_discount, params);
			  break;
			default:
			  $ws.grid_range_alternate_table($ws.product_volume_discount, params);
			  break;
		  }
		} else {
		  // This is used for collection listing page
		  var value_range = [];
		  for (var ee = 0; ee < $ws.product_volume_discount.volume_discount.length; ee++) {
			var v = $ws.product_volume_discount.volume_discount[ee];
			value_range.push(Number(v.value));
		  }
		  var min = Math.min.apply(null, value_range);
		  var max = Math.max.apply(null, value_range);
		  log("Min discount: " + min);
		  log("Max discount: " + max);
		  var max_discount_index = $ws.product_volume_discount.volume_discount.findIndex(function (b) {
			return b.value == max
		  });
		  if (max_discount_index > -1) {
			var max_discount = $ws.product_volume_discount.volume_discount[max_discount_index];
			var price = Number($ws.mF($ws.selected_variant.price));
			var discount = $ws.get_discount(price, max_discount.type, max_discount.value);
			if (Number(discount) == 0 || (Number(price) == 0)) {
			  $ws.vd_has_discount = false;
			}
			if ($ws.vd_has_discount) {
			  var compare_at_price = Number($ws.mF($ws.selected_variant.price));
			  if ($ws.general_settings.show_compare_at_price) {
				compare_at_price = Number($ws.mF($ws.selected_variant.compare_at_price));
			  }
			  if (isNaN($ws.selected_variant.compare_at_price) || (Number($ws.selected_variant.compare_at_price) <= Number($ws.selected_variant.price))) {
				compare_at_price = 0;
				if ((Number(price) - Number(discount)) < Number(price)) {
				  compare_at_price = Number(price).toFixed(2);
				}
			  }
			  var regular_price = (Number(price) - Number(discount)).toFixed(2);
			  compare_at_price = Number(compare_at_price).toFixed(2);
        console.log(regular_price,compare_at_price)
			  if (Number(compare_at_price) < 1) {
				compare_at_price = "";
			  }
			  if (!$ws.general_settings.compare_at_price) {
				compare_at_price = "";
			  }
			  var html = '';
			  if ($ws.template === 'product') {
				html = $ws.product_price_html;
			  } else {
				html = $ws.hcsr_qb_price_html;
			  }
              if (!$ws.general_settings.compare_at_price) {
				compare_at_price = "";
				html = WSAIO.remove_comparePrice(html);
			  }
			  try {
				compare_at_price = compare_at_price.replace("$", "$@");
				regular_price = regular_price.replace("$", "$@");
			  } catch (e) {
				log(e)
			  }
			  html = html
			  .replace(new RegExp("%regular_price%", "gi"), $ws.Lang.from + " " + regular_price)
			  .replace(new RegExp("%compare_at_price%", "gi"), compare_at_price);
			  try {
				html = html.replace(/@/g, '');
			  } catch (e) {
				log(e);
			  }
			  if (params.item_price_selector && $ws.selected_product.id) {
				$(params.item_price_selector).html(html)
			  } else {
				isError("Item price selector is undefined");
			  }			  
			}
		  }
		}
		$ws.saleClock($ws.product_volume_discount, $ws.selected_product, "vd");
	  }
	  if (typeof $ws.callback === 'function') {
		$ws.callback({
		  product_volume_discount: $ws.product_volume_discount,
		  quantity_breaks: $ws.product_qb,
		  product: $ws.selected_product,
		  variant: $ws.selected_variant
		});
	  }
	  // response back
	  if (typeof callback === 'function') {
		callback({
		  product_volume_discount: $ws.product_volume_discount,
		  quantity_breaks: $ws.product_qb,
		  product: $ws.selected_product,
		  variant: $ws.selected_variant
		});
	  }
	  // end for loop
	};

	WSAIO.regularDiscount = function (params, callback) {
	  if (WSAIO.debugger) debugger;
	  if (typeof params === 'function') callback = params;
	  if(!params){
		isError("No params provided for regular discount");
		return false;
	  }
	  if(!params.item_price_selector){
		isError("Item price selector is undefined");
		return false;
	  }
	  if (WSAIO.isWholesaleCustomer() === false) {
		if (typeof callback === "function") {
		  return callback({
			error: "Invalid customer"
		  });
		} else {
		  return "Invalid customer";
		}
	  }
	  if($(params.item_price_selector).attr('class') != undefined){
		if($(params.item_price_selector).attr('class').indexOf('wsaio_') == -1){
		  $(params.item_price_selector).addClass('wsaio_'+params.product_id)
		}
	  }
      var WSAIO_variant_sku_are_same = true;

      if (typeof params.template !== 'undefined' && typeof WSAIO.auto_vat_enable != 'undefined' &&  WSAIO.auto_vat_enable){
		   var _id = params.product_id;
		   if(WSAIO.template != 'cart' && typeof WSAIO.product_in_collects != 'undefined' && WSAIO.product_in_collects.length != 0){
				var _indx = WSAIO.product_in_collects.findIndex(function(e,r){
					return e.product_id == _id;
				});
				if(typeof WSAIO.product_in_collects[_indx] != 'undefined' && typeof WSAIO.product_in_collects[_indx].product_variants != 'undefined'){
			     	var  current_product_variant = WSAIO.product_in_collects[_indx].product_variants;
					var _indx_of_variant = current_product_variant.findIndex(function(d,t){
						return d.id == params.variant_id;
					});
					WSAIO.selected_variant_taxable = current_product_variant[_indx_of_variant].taxable;
					// if Price set in price adjustment, then no tax include function run
					if(typeof WSAIO.tax_settings != 'undefined' && WSAIO.tax_settings.presentment != null && WSAIO.tax_settings.presentment.price_adjustments != null && WSAIO.tax_settings.presentment.price_adjustments.fixed_variant_ids.length && WSAIO.tax_settings.presentment.price_adjustments.fixed_variant_ids.indexOf(current_product_variant[_indx_of_variant].id) > -1){
						WSAIO.selected_variant_taxable = false;
					}
				}
			}else if(typeof WSAIO.products_with_collections != 'undefined' && WSAIO.products_with_collections.length != 0){
				var _indx = WSAIO.products_with_collections.findIndex(function(e,r){
					return e.product_id == _id;
				});
				if(typeof WSAIO.products_with_collections[_indx] != 'undefined' && typeof WSAIO.products_with_collections[_indx].product_variants != 'undefined'){
					var  current_product_variant = WSAIO.products_with_collections[_indx].product_variants;
					var _indx_of_variant = current_product_variant.findIndex(function(d,t){
						return d.id == params.variant_id;
					})
					WSAIO.selected_variant_taxable = current_product_variant[_indx_of_variant].taxable;
					// if Price set in price adjustment, then no tax include function run
					if(typeof WSAIO.tax_settings != 'undefined' && WSAIO.tax_settings.presentment != null && WSAIO.tax_settings.presentment.price_adjustments != null && WSAIO.tax_settings.presentment.price_adjustments.fixed_variant_ids.length && WSAIO.tax_settings.presentment.price_adjustments.fixed_variant_ids.indexOf(current_product_variant[_indx_of_variant].id) > -1){
						WSAIO.selected_variant_taxable = false;
					}
				}
			}
      }

	  $ws.output = true;
	  $ws.selected_variant = JSON.parse(JSON.stringify($ws.current_variant)); // current variant obj should not change
	  $ws.selected_product = JSON.parse(JSON.stringify($ws.current_product)); // current product obj should not change
	  $ws.selected_collection = JSON.parse(JSON.stringify($ws.current_collection)); // current collection obj should not change
	  if (typeof params === 'object') {
		if (typeof params.product_id !== 'undefined' && params.product_id !== "default") $ws.selected_product.id = params.product_id;
		if (typeof params.product_handle !== 'undefined' && params.product_handle !== "default") $ws.selected_product.handle = params.product_handle;
		if (typeof params.variant_id !== 'undefined' && params.variant_id !== "default") $ws.selected_variant.id = params.variant_id;
		if (typeof params.variant_sku !== 'undefined' && params.variant_sku !== "default") $ws.selected_variant.sku = params.variant_sku;
		if (typeof params.variant_price !== 'undefined' && params.variant_id !== "default") $ws.selected_variant.price = params.variant_price;
