			var excluded_actual_regularPrice = 0;
              var check_compare = 0, check_applied_discount = 0;
              if(WSAIO.exclude_VAT != 0 && WSAIO.check_eligible_customer()){
                var exclude_value = (WSAIO.exclude_VAT/100) + 1;
