  WSAIO.getClockTextByType = function (ends_in, schedule, type) {
	function get_date_time_value(date, time) {
        // Parsing date to YYYY/MM/DD HH:MM:SS from YYYY-MM-DD HH:MM:SS
        if(typeof date === "string")date = date.replace(/-/g, "/");

	  var d = new Date(date),
		  year = d.getFullYear(),
		  month = d.getMonth() + 1,
		  dat = d.getDate();
	  return year + "-" + month + "-" + dat + " " + time;
	}
	var ends_message = '';
	try {
	  switch (type) {
		case "1":
		  if (ends_in.days > 0) {
			ends_message += ends_in.days + (ends_in.days == 1 ? 'day, ' : 'days, ');
		  }
		  if (ends_in.hours > 0) {
			ends_message += ends_in.hours + (ends_in.hours == 1 ? 'hour, ' : 'hours, ');
		  }
		  if (ends_in.minutes > 0) {
			var minutes = ends_in.minutes;
			if (minutes < 10) {
			  minutes = '0' + minutes;
			}
			ends_message += minutes + (ends_in.minutes == 1 ? 'minute, ' : 'minutes, ');
		  }
		  if (ends_in.seconds >= 0) {
			var seconds = ends_in.seconds;
			if (seconds < 10) {
			  seconds = '0' + seconds;
			}
			ends_message += '<span class="wsaio--seconds">' + seconds + '</span>' + (ends_in.seconds <= 1 ? 'second' : 'seconds');
		  }
		  break;
		case '2':
		  if (ends_in.days > 0) {
			ends_message += ends_in.days + (ends_in.days == 1 ? ' day' : ' days');
		  } else if (ends_in.days == 0) {
			if (ends_in.hours > 0) {
			  ends_message += ends_in.hours + (ends_in.hours == 1 ? 'hour' : 'hours');
			}
		  } else {
			ends_message += "";
		  }
		  break;
		case '3':
		  var options = {
			timeZone: 'America/New_York',
			weekday: 'long',
			year: 'numeric',
			month: 'long',
			day: 'numeric',
			hour: 'numeric',
			minute: 'numeric',
			second: 'numeric'
		  };
		  ends_message += new Date(get_date_time_value(schedule.end_date, schedule.end_time_est)).toLocaleDateString('en-UK', options);
		  break;
		case '4':
		  var options = {
			timeZone: 'America/New_York',
			weekday: 'long',
			year: 'numeric',
			month: 'long',
			day: 'numeric'
		  };
		  ends_message += new Date(get_date_time_value(schedule.end_date, schedule.end_time_est)).toLocaleDateString('en-UK', options);
		  break;
		case '5':
		  var options = {
			timeZone: 'America/New_York',
			year: 'numeric',
			month: 'long',
			day: 'numeric'
		  };
		  ends_message += new Date(get_date_time_value(schedule.end_date, schedule.end_time_est)).toLocaleDateString('en-UK', options);
		  break;
		case '6':
		  var options = {
			timeZone: 'America/New_York',
			year: 'numeric',
			month: 'short',
			day: 'numeric',
			hour: 'numeric',
			minute: 'numeric',
			second: 'numeric'
		  };
		  ends_message += new Date(get_date_time_value(schedule.end_date, schedule.end_time_est)).toLocaleDateString('en-UK', options);
		  break;
		case '7':
		  var options = {
			timeZone: 'America/New_York',
			year: 'numeric',
			month: 'short',
			day: 'numeric'
		  };
		  ends_message += new Date(get_date_time_value(schedule.end_date, schedule.end_time_est)).toLocaleDateString('en-UK', options);
		  break;
		case '8':
		  if (ends_in.days > 0) {
			ends_message += ends_in.days + 'd:';
		  }
		  if (ends_in.hours > 0) {
			ends_message += ends_in.hours + 'h:';
		  }
		  if (ends_in.minutes > 0) {
			var minutes = ends_in.minutes;
			if (minutes < 10) {
			  minutes = '0' + minutes;
			}
			ends_message += minutes + 'm:';
		  }
		  if (ends_in.seconds >= 0) {
			var seconds = ends_in.seconds;
			if (seconds < 10) {
			  seconds = '0' + seconds;
			}
			ends_message += '<span class="wsaio-seconds">' + seconds + '</span>' + 's';
		  }
		  break;
		case '9':
		  if (ends_in.days > 0) {
			ends_message += ends_in.days + (ends_in.days == 1 ? 'day ' : 'days ');
		  }
		  if (ends_in.hours > 0) {
			ends_message += ends_in.hours + (ends_in.hours == 1 ? 'hour ' : 'hours ');
		  }
		  if (ends_in.minutes > 0) {
			var minutes = ends_in.minutes;
			if (minutes < 10) {
			  minutes = '0' + minutes;
			}
			ends_message += minutes + (ends_in.minutes == 1 ? 'minute ' : 'minutes ');
		  }
		  if (ends_in.seconds >= 0) {
			var seconds = ends_in.seconds;
			if (seconds < 10) {
			  seconds = '0' + seconds;
			}
			ends_message += '<span class="wsaio-seconds">' + seconds + '</span>' + (ends_in.seconds <= 1 ? 'second' : 'seconds');
		  }
		  break;
		default:
		  break;
	  }
	} catch (e) {
	  log(e);
	}
	return ends_message;
  };

