		 product_handle = handle.split("?")[0].split("/")[
		   handle.split("/").length - 1
		 ];
	   } catch (e) {}
	 }

	 jQuery.getJSON(WSAIO.local_param + "products/" + product_handle + ".js", function (product) {
	   window.current_product = product;
