                }
              })
            }

          setTimeout(function(){
                try{
                  if(WSAIO != undefined && !WSAIO.main_liquid_file_included && (typeof WSAIO.isShowCheckout == 'undefined')){
                    $(WSAIO.checkout_selector+','+WSAIO.duplicate_checkout_selector).removeClass('wsaio_hideCheckout')
                    $('.wsaio_coupon_group, .wsaio_coupon_link').css('cssText','display:block !important')
                }
                }catch(e){}
                try{
                  if((WSAIO != undefined || WSAIO.main_liquid_file_included) && (typeof WSAIO.isShowCheckout == 'undefined')){
                      $(WSAIO.checkout_selector+','+WSAIO.duplicate_checkout_selector).removeClass('wsaio_hideCheckout')
                        $('.wsaio_coupon_group, .wsaio_coupon_link').css('cssText','display:block !important')
                    }
                    }catch(e){}
                try{
                  if(WSAIO == undefined || !WSAIO.isWholesaleCustomer() && (typeof WSAIO.isShowCheckout == 'undefined')){
                      $(WSAIO.checkout_selector+','+WSAIO.duplicate_checkout_selector).removeClass('wsaio_hideCheckout')
                        $('.wsaio_coupon_group, .wsaio_coupon_link').css('cssText','display:block !important')
                    }
                    }catch(e){}
                    if(WSAIO.buy_now_btn_replace == false || typeof WSAIO.buyNowEventListener == 'undefined' ){
                    $('.shopify-payment-button .shopify-payment-button__button').css('cssText','display:inline-block !important')
                    }
                    $('.wsaio_loader_checkout').hide();
            },1000);
            if(typeof current_product != 'undefined' && current_product != null){
              $('.wsaio_'+current_product.id).parents(WSAIO.product_parent_grid_selector).find('.wsaio_buynow').css('cssText','display:inline-block !important')
              if(WSAIO.buy_now_btn_replace == false){
              $(WSAIO.product_parent_grid_selector).find('.wsaio_buynow').remove()
              $('.wsaio_'+current_product.id).parents(WSAIO.product_parent_grid_selector).find('.shopify-payment-button .shopify-payment-button__button').css('cssText','display:inline-block !important')
              }
            }
          },1050)
        }else{
          $(WSAIO.checkout_selector+','+WSAIO.duplicate_checkout_selector).removeClass('wsaio_hideCheckout')
            $('.wsaio_loader_checkout').hide();
            $('.shopify-payment-button .shopify-payment-button__button').css('cssText','display:inline-block !important')
        }
        });

      WSAIO.run_checkout = function(){

        if(WSAIO.app_mode_applied){
        $('.wsaio_coupon_group, .wsaio_coupon_link').css('cssText','display:block !important')
        WSAIO.checkoutAlert = $('<ol class="wsaio-alerts"></ol>')

        if(WSAIO.replace_checkout){
          $(WSAIO.replace_checkout_button).replaceWith(WSAIO.duplicate_checkout_html)
          if(typeof WSAIO.checkoutEventListner != 'undefined' && typeof WSAIO.isWholesaleCustomer != 'undefined' && WSAIO.isWholesaleCustomer() !== false){
            WSAIO.checkoutEventListner()
          }else{
            if(WSAIO.replace_checkout){
              $(document).on('click',WSAIO.checkout_selector+','+WSAIO.duplicate_checkout_selector, function (e) {
                e.preventDefault();
                e.stopImmediatePropagation();
                e.stopPropagation();
                window.location.href = '/checkout';
              });
            }
          }

        }
        {% assign runOC = true %}
        {% for order_Cdata in shop.metafields.waioMOC %}
          {% assign COdata = order_Cdata | last %}
          {% assign check_OCrules = COdata | size %}
          {% if check_OCrules == 0 %}
          {% assign runOC = false %}
          {% endif %}
         {% endfor %}
        {% if runOC %} WSAIO.order_control();WSAIO.disableCheckout(){% endif %}
        if(WSAIO.default_limit){
          if(typeof WSAIO.allow_normal_checkout__OC == 'undefined' || WSAIO.allow_normal_checkout__OC == false){
             WSAIO.checkoutAlert.append('<li class="waio-orderControl-message">' + WSAIO.checkout_message + "</li>");
          }else{
            WSAIO.checkoutAlert.append('<li class="waio-orderControl-message">' + WSAIO.checkout_message + "</li>");
             WSAIO.checkoutAlert.append('<li class="waio-orderControl-message">Fulfill minimum order requirements to avail discount</li>');
          }
        }

        if(WSAIO.applied_shipping != undefined && WSAIO.applied_shipping.apply_shipping_charges && $('.Shipping_msg').length == 0 && WSAIO.shipping_message_applied){
          WSAIO.checkoutAlert.after('<div class="Shipping_msg" style="font-weight: 600;font-weight: bold; font-style: italic">' + WSAIO.applied_shipping.final_ship_rule.message + "</div>");
        }

        {% if show_button %}
          if($('.waio__net--wrapper').length == 0){
            $(WSAIO.checkout_message_selector).after('<div class="waio__net--wrapper"><div class="btn_stng"><div class="demo_instruction align_mig"><p class="texttt">'+net_cart_label+'</p></div><a href="'+Shopify.routes.root+'pages/net-term-order" name="netTerm" class=" btn btn-lg align_mig btn" > '+net_btn_label+'</a> </div>  </div>')
          }
          $(WSAIO.netTerm_checkout_selector).removeClass('wsaio_hideCheckout')
         {% endif %}
        if(typeof WSAIO.isShowCheckout == 'undefined'){
          $(WSAIO.checkout_selector+','+WSAIO.duplicate_checkout_selector).removeClass('wsaio_hideCheckout')
        }else{
          $(WSAIO.checkout_selector+','+WSAIO.duplicate_checkout_selector).addClass('wsaio_hideCheckout')
        }
        $('.wsaio_coupon_group, .wsaio_coupon_link').css('cssText','display:block !important')

        $('.wsaio-alerts').remove();
        $('.waio_agree_message').remove()
        if(typeof window.MOQ_alert != 'undefined' && window.MOQ_alert!=''){
          WSAIO.checkoutAlert = window.MOQ_alert;
        }else{
          if($('.wsaio-alerts').length == 0 && WSAIO.checkoutAlert != undefined){
            $(WSAIO.checkout_message_selector).after(WSAIO.checkoutAlert);
          }
        }
        WSAIO.cartLevelUpSell();
        $('.wsaioUsellMessage,.Shipping_msg').remove();
        if(WSAIO.wsaioUpSell_msg != undefined && WSAIO.wsaioUpSell_msg!= '' && $('.wsaioUsellMessage').length == 0){
          if(typeof WSAIO.cart_level_upsell_message_select == 'undefined' || WSAIO.cart_level_upsell_message_select == ''){
             $(WSAIO.checkout_message_selector).before('<div class="wsaioUsellMessage">' + WSAIO.wsaioUpSell_msg + "</div>");
          }else{
             $(WSAIO.cart_level_upsell_message_select).before('<div class="wsaioUsellMessage">' + WSAIO.wsaioUpSell_msg + "</div>");
          }
        }
        if(WSAIO.applied_shipping != undefined && WSAIO.applied_shipping.apply_shipping_charges && $('.Shipping_msg').length == 0 && WSAIO.shipping_message_applied){
          $(WSAIO.checkout_message_selector).after('<div class="Shipping_msg" style="font-weight: 600;font-weight: bold; font-style: italic">' + WSAIO.applied_shipping.final_ship_rule.message + "</div>");
        }
        if($('.wsaio-alerts').length == 0 && WSAIO.checkoutAlert != undefined && typeof window.MOQ_alert != 'undefined' && WSAIO.checkoutAlert.indexOf('<ul class="wsaio-alerts"><li') > -1){
          $(WSAIO.checkout_message_selector).after(WSAIO.checkoutAlert);
        }

        if(WSAIO.disable_checkout || window.disable_checkout_MOQ || window.isAgree == false){
                if(WSAIO.agrrement_alert_message != '' && $('.waio_agree_message').length == 0 && typeof window.isAgree != 'undefined' && window.isAgree == false){
                  $(WSAIO.checkout_message_selector).after('<p class="waio_agree_message" style="font-size: 14px;">'+WSAIO.agrrement_alert_message+'</p>');
                }
                if((typeof WSAIO.allow_normal_checkout__OC != 'undefined' && WSAIO.allow_normal_checkout__OC) && ((!window.disable_checkout_MOQ || $('.moq_error').length == 0) && (typeof window.isAgree == 'undefined' || window.isAgree))){
                      $(WSAIO.netTerm_checkout_selector).addClass('checkout-disabled').attr('disabled','disabled').css({'pointer-events': 'none','cursor': 'default'})
                }else{
                      $(WSAIO.checkout_selector+','+WSAIO.duplicate_checkout_selector+','+WSAIO.netTerm_checkout_selector).addClass('checkout-disabled').attr('disabled','disabled').css({'pointer-events': 'none','cursor': 'default'})
                }
        }else{
            $(WSAIO.checkout_selector+','+WSAIO.duplicate_checkout_selector+','+WSAIO.netTerm_checkout_selector).removeClass('checkout-disabled').removeAttr('disabled').css({'pointer-events': 'auto','cursor': 'pointer'})
        }

        $('.wsaio_loader_checkout').hide();
      }
    }
        window.addEventListener("load",function(){
          if(typeof WSAIO != 'undefined' && window.wsaio_involved != undefined && window.wsaio_involved == true && typeof WSAIO.preCheckout == 'undefined' && WSAIO.app_mode_applied){
            WSAIO.preloadCheckout = function (){
              WSAIO.showCheckoutLoader();
              try {
                    "undefined" === typeof window.waioMOC && (window.waioMOC = {});
                    "undefined" === typeof waioMOC.cart && (waioMOC.cart = {});
                    try {
                      window.cart_data = WSAIO.cart_object;
                      waioMOC.cart["item_count"] = WSAIO.cart_object.item_count;
                      waioMOC.cart["original_total_price"] = WSAIO.cart_object.original_total_price;
                      WSAIO.order_control()
                      setTimeout(function(){
                        if(typeof WSAIO.loadcartQuantityControllers != 'undefined' && typeof WSAIO.load_checkout_restriction != 'undefined'){ WSAIO.load_checkout_restriction(); WSAIO.loadcartQuantityControllers([],function(e){setTimeout(function(){WSAIO.run_checkout()},1000)});}else{WSAIO.run_checkout()}
                      },1000)
                    } catch (e) {}
                 } catch (e) {}

            };
            WSAIO.preCheckout = function (){
              if(typeof WSAIO.run_checkout != 'undefined'){
                WSAIO.showCheckoutLoader()
                 $.getJSON(WSAIO.local_param +'cart',function(e){
                  window.cart_data = e;
                    try {
                    "undefined" === typeof window.waioMOC && (window.waioMOC = {});
                    "undefined" === typeof waioMOC.cart && (waioMOC.cart = {});
                    try {                                       
                      waioMOC.cart["item_count"] = e.item_count;
                      waioMOC.cart["original_total_price"] = e.original_total_price;
                      WSAIO.order_control()
                      if(typeof WSAIO.cartQuantityControllers != 'undefined' && typeof WSAIO.checkout_restriction != 'undefined'){ WSAIO.checkout_restriction(); WSAIO.cartQuantityControllers([],function(e){setTimeout(function(){WSAIO.run_checkout()},500)});}else{WSAIO.run_checkout()}
                    } catch (e) {}
                    } catch (e) {}
                })
              }
            };
            WSAIO.preloadCheckout();
          }
        })

      };

      window.WSAIO_MODULE(jQuery)
</script>
